mapper.select:
select 

from GAME_BATCH_IMPORT_REWARD
where IMPORT_NO = #{importNo,jdbcType=CHAR}
  and REWARD_NO = #{rewardNo,jdbcType=DECIMAL}

mapper.delete:
delete from GAME_BATCH_IMPORT_REWARD
where IMPORT_NO = #{importNo,jdbcType=CHAR}
  and REWARD_NO = #{rewardNo,jdbcType=DECIMAL}

mapper.update:
update GAME_BATCH_IMPORT_REWARD
set PLAN_CODE = #{planCode,jdbcType=VARCHAR},
  BATCH_NO = #{batchNo,jdbcType=VARCHAR},
  FAST_IDENTITY_CODE = #{fastIdentityCode,jdbcType=VARCHAR},
  SINGLE_REWARD_AMOUNT = #{singleRewardAmount,jdbcType=DECIMAL},
  COUNTS = #{counts,jdbcType=DECIMAL}
where IMPORT_NO = #{importNo,jdbcType=CHAR}
  and REWARD_NO = #{rewardNo,jdbcType=DECIMAL}
