mapper.select:
select 

from GAME_BATCH_REWARD_DETAIL
where IMPORT_NO = #{importNo,jdbcType=CHAR}
  and PLAN_CODE = #{planCode,jdbcType=VARCHAR}
  and SAFE_CODE = #{safeCode,jdbcType=VARCHAR}

mapper.delete:
delete from GAME_BATCH_REWARD_DETAIL
where IMPORT_NO = #{importNo,jdbcType=CHAR}
  and PLAN_CODE = #{planCode,jdbcType=VARCHAR}
  and SAFE_CODE = #{safeCode,jdbcType=VARCHAR}

mapper.update:
update GAME_BATCH_REWARD_DETAIL


    BATCH_NO = #{batchNo,jdbcType=VARCHAR},


    IS_PAID = #{isPaid,jdbcType=DECIMAL},


where IMPORT_NO = #{importNo,jdbcType=CHAR}
  and PLAN_CODE = #{planCode,jdbcType=VARCHAR}
  and SAFE_CODE = #{safeCode,jdbcType=VARCHAR}

update GAME_BATCH_REWARD_DETAIL
set BATCH_NO = #{batchNo,jdbcType=VARCHAR},
  IS_PAID = #{isPaid,jdbcType=DECIMAL}
where IMPORT_NO = #{importNo,jdbcType=CHAR}
  and PLAN_CODE = #{planCode,jdbcType=VARCHAR}
  and SAFE_CODE = #{safeCode,jdbcType=VARCHAR}
