mapper.select:
select 

from ITEM_QUANTITY
where ITEM_CODE = #{itemCode,jdbcType=CHAR}
  and WAREHOUSE_CODE = #{warehouseCode,jdbcType=CHAR}

mapper.delete:
delete from ITEM_QUANTITY
where ITEM_CODE = #{itemCode,jdbcType=CHAR}
  and WAREHOUSE_CODE = #{warehouseCode,jdbcType=CHAR}

mapper.update:
update ITEM_QUANTITY


    ITEM_NAME = #{itemName,jdbcType=VARCHAR},


    ITEMS = #{items,jdbcType=DECIMAL},


where ITEM_CODE = #{itemCode,jdbcType=CHAR}
  and WAREHOUSE_CODE = #{warehouseCode,jdbcType=CHAR}

update ITEM_QUANTITY
set ITEM_NAME = #{itemName,jdbcType=VARCHAR},
  ITEMS = #{items,jdbcType=DECIMAL}
where ITEM_CODE = #{itemCode,jdbcType=CHAR}
  and WAREHOUSE_CODE = #{warehouseCode,jdbcType=CHAR}
