mapper.select:
select 

from ITEM_ISSUE_DETAIL
where II_NO = #{iiNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}

mapper.delete:
delete from ITEM_ISSUE_DETAIL
where II_NO = #{iiNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}

mapper.update:
update ITEM_ISSUE_DETAIL


    ITEM_CODE = #{itemCode,jdbcType=CHAR},


    QUANTITY = #{quantity,jdbcType=DECIMAL},


where II_NO = #{iiNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}

update ITEM_ISSUE_DETAIL
set ITEM_CODE = #{itemCode,jdbcType=CHAR},
  QUANTITY = #{quantity,jdbcType=DECIMAL}
where II_NO = #{iiNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}
