mapper.select:
select 

from SALE_RETURN_RECODER_DETAIL
where RETURN_NO = #{returnNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}

mapper.delete:
delete from SALE_RETURN_RECODER_DETAIL
where RETURN_NO = #{returnNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}

mapper.update:
update SALE_RETURN_RECODER_DETAIL


    PLAN_CODE = #{planCode,jdbcType=VARCHAR},


    PACKAGES = #{packages,jdbcType=DECIMAL},


    AMOUNT = #{amount,jdbcType=DECIMAL},


where RETURN_NO = #{returnNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}

update SALE_RETURN_RECODER_DETAIL
set PLAN_CODE = #{planCode,jdbcType=VARCHAR},
  PACKAGES = #{packages,jdbcType=DECIMAL},
  AMOUNT = #{amount,jdbcType=DECIMAL}
where RETURN_NO = #{returnNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}
