mapper.select:
select 

from WH_GOODS_ISSUE_DETAIL
where SGI_NO = #{sgiNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}

mapper.delete:
delete from WH_GOODS_ISSUE_DETAIL
where SGI_NO = #{sgiNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}

mapper.insert:
insert into WH_GOODS_ISSUE_DETAIL (SGI_NO, SEQUENCE_NO, ISSUE_TYPE, 
  REF_NO, VALID_NUMBER, PLAN_CODE, 
  BATCH_NO, TRUNK_NO, BOX_NO, 
  PACKAGE_NO, TICKETS, AMOUNT
  )
values (#{sgiNo,jdbcType=CHAR}, #{sequenceNo,jdbcType=DECIMAL}, #{issueType,jdbcType=DECIMAL}, 
  #{refNo,jdbcType=CHAR}, #{validNumber,jdbcType=DECIMAL}, #{planCode,jdbcType=VARCHAR}, 
  #{batchNo,jdbcType=VARCHAR}, #{trunkNo,jdbcType=VARCHAR}, #{boxNo,jdbcType=DECIMAL}, 
  #{packageNo,jdbcType=VARCHAR}, #{tickets,jdbcType=DECIMAL}, #{amount,jdbcType=DECIMAL}
  )
