mapper.select:
select * from ( select tab.*,rownum rn from (SELECT A.AGENCY_CODE, A.ACC_NAME, A.ACC_STATUS, A.ACC_NO, A.CREDIT_LIMIT, A.ACCOUNT_BALANCE, b.AGENCY_NAME, c.PLAN_CODE, c.SALE_COMM, c.PAY_COMM FROM ACC_AGENCY_ACCOUNT a LEFT JOIN INF_AGENCYS b ON (a.AGENCY_CODE = b.AGENCY_CODE) LEFT JOIN GAME_AGENCY_COMM_RATE c ON (a.AGENCY_CODE = c.AGENCY_CODE) WHERE 1 = 1
AND a.AGENCY_CODE = #{agencyCode}
and B.AGENCY_NAME = #{agencyName}
and A.ACC_STATUS!=2 and B.status = 1 and B.ORG_CODE = #{orgCode} ORDER BY A.AGENCY_CODE DESC ) TAB ) where rn > ${beginNum} and rn <= ${endNum}

SELECT count(1)
FROM ACC_AGENCY_ACCOUNT a left join INF_AGENCYS b ON (a.AGENCY_CODE = b.AGENCY_CODE)
WHERE 1 = 1

	AND a.AGENCY_CODE = #{agencyCode}


	and B.AGENCY_NAME = #{agencyName}

and B.ORG_CODE = #{orgCode}

mapper.update:
UPDATE ACC_AGENCY_ACCOUNT 
SET CREDIT_LIMIT = #{creditLimit}
WHERE AGENCY_CODE = #{agencyCode}
