mapper.select:
select 

from ITEM_CHECK_DETAIL_BE
where CHECK_NO = #{checkNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}

mapper.delete:
delete from ITEM_CHECK_DETAIL_BE
where CHECK_NO = #{checkNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}

mapper.update:
update ITEM_CHECK_DETAIL_BE


    ITEM_CODE = #{itemCode,jdbcType=CHAR},


    QUANTITY = #{quantity,jdbcType=DECIMAL},


where CHECK_NO = #{checkNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}

update ITEM_CHECK_DETAIL_BE
set ITEM_CODE = #{itemCode,jdbcType=CHAR},
  QUANTITY = #{quantity,jdbcType=DECIMAL}
where CHECK_NO = #{checkNo,jdbcType=CHAR}
  and SEQUENCE_NO = #{sequenceNo,jdbcType=DECIMAL}
