mapper.select:
select 

from WH_TICKET_TRUNK
where PLAN_CODE = #{planCode,jdbcType=VARCHAR}
  and BATCH_NO = #{batchNo,jdbcType=VARCHAR}
  and TRUNK_NO = #{trunkNo,jdbcType=VARCHAR}

mapper.delete:
delete from WH_TICKET_TRUNK
where PLAN_CODE = #{planCode,jdbcType=VARCHAR}
  and BATCH_NO = #{batchNo,jdbcType=VARCHAR}
  and TRUNK_NO = #{trunkNo,jdbcType=VARCHAR}
