mapper.select:
WITH AGENCY_ACCOUNT AS( SELECT * FROM (SELECT tab.*, ROWNUM rn FROM ( SELECT A.AGENCY_CODE, A.ACC_NAME, A.ACC_STATUS, A.ACC_NO, A.CREDIT_LIMIT, A.ACCOUNT_BALANCE, b.AGENCY_NAME FROM ACC_AGENCY_ACCOUNT a JOIN INF_AGENCYS b ON (a.AGENCY_CODE = b.AGENCY_CODE) WHERE A.ACC_STATUS != 2 AND B.status = 1
AND a.AGENCY_CODE = #{agencyCode}
and B.AGENCY_NAME = #{agencyName}
and B.ORG_CODE = #{orgCode}
ORDER BY A.AGENCY_CODE DESC) TAB) where rn > ${beginNum} and rn <= ${endNum} ) SELECT A.AGENCY_CODE, A.ACC_NAME, A.ACC_STATUS, A.ACC_NO, A.CREDIT_LIMIT, A.ACCOUNT_BALANCE, A.AGENCY_NAME, c.PLAN_CODE, c.SALE_COMM, c.PAY_COMM FROM AGENCY_ACCOUNT a LEFT JOIN GAME_AGENCY_COMM_RATE c ON (a.AGENCY_CODE = c.AGENCY_CODE) ORDER BY A.AGENCY_CODE DESC

SELECT count(1)
FROM ACC_AGENCY_ACCOUNT a left join INF_AGENCYS b ON (a.AGENCY_CODE = b.AGENCY_CODE)
WHERE A.ACC_STATUS != 2 AND B.status = 1

	AND a.AGENCY_CODE = #{agencyCode}


	and B.AGENCY_NAME = #{agencyName}


	and B.ORG_CODE = #{orgCode}

mapper.update:
UPDATE ACC_AGENCY_ACCOUNT 
SET CREDIT_LIMIT = #{creditLimit}
WHERE AGENCY_CODE = #{agencyCode}
